import { useEffect, useState } from "react";
import { Toaster, toast } from "react-hot-toast";

const host = "api.frankfurter.app";

export default function App() {
  const [amount, setAmount] = useState("");
  const [from, setFrom] = useState("");
  const [to, setTo] = useState("");
  const [currencies, setCurrencies] = useState([]);

  const validateData = () => {
    let isValid = true;
    if (!from || !to || from === to || Number(amount) === 0) {
      isValid = false;
    }
    return isValid;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateData()) {
      try {
        const apiCall = fetch(
          `https://${host}/latest?amount=${amount}&from=${from}&to=${to}`
        );
        // toast.promise(apiCall, {
        //   loading: "Converting",
        //   success: (data) => data.json().then(console.log(data)),
        //   error: (err) => console.error(err),
        // });

        toast.promise(
          myPromise,
          {
            loading: 'Loading',
            success: (data) => `Successfully saved ${data.name}`,
            error: (err) => `This just happened: ${err.toString()}`,
          },
          {
            style: {
              minWidth: '250px',
            },
            success: {
              duration: 5000,
              icon: 'ðŸ”¥',
            },
          }
        );
      } catch (err) {
        console.error(err);
      }
    }
  };

  useEffect(() => {
    fetch(`https://${host}/currencies`)
      .then((response) => response.json())
      .then((data) => {
        const currencyArray = Object.entries(data).map(([code, name]) => ({
          code,
          name,
        }));
        setCurrencies(currencyArray);
      })
      .catch((error) => {
        console.error("Error fetching currencies:", error);
      });
  }, []);

  return (
    <>
      <Toaster position="top-center" />
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={amount}
          onChange={(e) => {
            const inputValue = e.target.value.trim();
            const isValidNumber = !isNaN(inputValue);

            setAmount((prevValue) => (isValidNumber ? inputValue : prevValue));
          }}
          placeholder="Enter the amount to convert"
        />

        <select value={from} onChange={(e) => setFrom(e.target.value)}>
          <option value="">Select Currency</option>
          {currencies.map((currency) => (
            <option key={currency.code} value={currency.code}>
              {currency.code} - {currency.name}
            </option>
          ))}
        </select>

        <select value={to} onChange={(e) => setTo(e.target.value)}>
          <option value="">Select Currency</option>
          {currencies.map((currency) => (
            <option key={currency.code} value={currency.code}>
              {currency.code} - {currency.name}
            </option>
          ))}
        </select>

        <button type="submit">Convert</button>
      </form>
    </>
  );
}
