import { useEffect, useState } from "react";
import { Toaster, toast } from "react-hot-toast";

const host = "api.frankfurter.app";

export default function App() {
  const [amount, setAmount] = useState("");
  const [from, setFrom] = useState("");
  const [to, setTo] = useState("");
  const [currencies, setCurrencies] = useState([]);
  const [result, setResult] = useState("");

  const validateData = () => {
    let isValid = true;
    if (!from || !to || from === to || Number(amount) === 0) {
      isValid = false;
    }
    return isValid;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (validateData()) {
      try {
        const response = await fetch(
          `https://${host}/latest?amount=${amount}&from=${from}&to=${to}`
        );

        if (!response.ok) {
          throw new Error(`Failed to convert. Status: ${response.status}`);
        }

        const data = await response.json();

        setResult(
          Object.entries(data.rates).map(([code, name]) => ({
            code,
            name,
          }))
        );

        toast.success("Conversion successful");
      } catch (err) {
        console.error(err);
        toast.error("Conversion failed");
      }
    }
  };

  useEffect(() => {
    fetch(`https://${host}/currencies`)
      .then((response) => response.json())
      .then((data) => {
        const currencyArray = Object.entries(data).map(([code, name]) => ({
          code,
          name,
        }));
        setCurrencies(currencyArray);
      })
      .catch((error) => {
        console.error("Error fetching currencies:", error);
      });
  }, []);

  return (
    <>
      <Toaster position="top-center" />
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          value={amount}
          onChange={(e) => {
            const inputValue = e.target.value.trim();
            const isValidNumber = !isNaN(inputValue);

            setAmount((prevValue) => (isValidNumber ? inputValue : prevValue));
            setResult("");
          }}
          placeholder="Enter the amount to convert"
        />

        <select
          value={from}
          onChange={(e) => {
            setFrom(e.target.value);
            setResult("");
          }}>
          <option value="">Select Currency</option>
          {currencies.map((currency) => (
            <option key={currency.code} value={currency.code}>
              {currency.code} - {currency.name}
            </option>
          ))}
        </select>

        <select
          value={to}
          onChange={(e) => {
            setTo(e.target.value);
            setResult("");
          }}>
          <option value="">Select Currency</option>
          {currencies.map((currency) => (
            <option key={currency.code} value={currency.code}>
              {currency.code} - {currency.name}
            </option>
          ))}
        </select>

        <button type="submit">Convert</button>
      </form>
    </>
  );
}
